import React, { useState, useEffect } from 'react';
import Markdown from 'markdown-to-jsx';
import Card from '../comps/Card';
import { Cards } from '../style/comps';
import sample from '../photos/sample.jpeg';

const useFetch = (url) => {

    const [content, setContent] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(async () => {
        const response = await fetch(url);
        const data = await response.json();
        setContent(data);
        setLoading(false);
    }, []);

    return { content, loading }
}

const Home = () => {

    const { content, loading } = useFetch('http://localhost:3500/articles/all');

    console.log(content);

    return (<div>
        <Cards>
            {loading ? <div>...loading</div> : content.map((info) => {
                return(<Card title={info.card_title} photo={info.card_image_link} desc={info.card_desc}></Card>)
            })}
        </Cards>
        <Markdown options={{ wrapper: 'article' }}># Hello!\n\nok</Markdown>
    </div>);
}

export default Home;
