import styled from 'styled-components'
import colors from './colors'

export const Cards = styled.div`

    border: red dotted;
    display: flex;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    grid-gap: 10px;
    padding: 10px;
    margin-left: auto;
    margin-right: auto;
    max-width: 90%;

`
