import styled from 'styled-components'

export const Background = styled.div`

  position: absolute;
  margin: 0;
  padding: 10;
  height: 100%;
  width: 100%;

  background: repeating-linear-gradient(
  45deg,
  #cfcfff,
  #cfcfff 5px,
  #c7c7ff 5px,
  #c7c7ff 10px
  );

`
